select l_orderkey, o_orderdate, o_shippriority, sum(l_extendedprice * (1 - l_discount)) as revenue from customer, orders, lineitem where c_mktsegment = 'FURNITURE' and c_custkey = o_custkey and l_orderkey = o_orderkey and o_orderdate < TO_DATE('1995-03-17','YYYY-MM-DD') and l_shipdate > TO_DATE('1995-03-17','YYYY-MM-DD') group by l_orderkey, o_orderdate, o_shippriority order by revenue desc, o_orderdate LIMIT 10;

select n_name, sum(l_extendedprice * (1 - l_discount)) as revenue from customer, orders, lineitem, supplier, nation, region where c_custkey = o_custkey and l_orderkey = o_orderkey and l_suppkey = s_suppkey and c_nationkey = s_nationkey and s_nationkey = n_nationkey and n_regionkey = r_regionkey and r_name = 'EUROPE' and o_orderdate >= TO_DATE('1993-01-01','YYYY-MM-DD') and o_orderdate < TO_DATE('1994-01-01','YYYY-MM-DD') group by n_name order by revenue desc;

select sum(l_extendedprice * l_discount) as revenue from lineitem where l_shipdate >= TO_DATE('1993-01-01','YYYY-MM-DD') and l_shipdate < TO_DATE('1994-01-01','YYYY-MM-DD') and l_discount >= 0.08 and l_discount < 0.1 and l_quantity < 24;

select supp_nation, cust_nation, l_year, sum(volume) as revenue from (select n1.n_name as supp_nation, n2.n_name as cust_nation, extract(year from l_shipdate) as l_year, l_extendedprice * (1 - l_discount) as volume from supplier, lineitem, orders, customer, nation n1, nation n2 where s_suppkey = l_suppkey and o_orderkey = l_orderkey and c_custkey = o_custkey and s_nationkey = n1.n_nationkey and c_nationkey = n2.n_nationkey and ((n1.n_name = 'RUSSIA' and n2.n_name = 'IRAN') or (n1.n_name = 'IRAN' and n2.n_name = 'RUSSIA')) and l_shipdate between date '1995-01-01' and date '1996-12-31') as shipping group by supp_nation, cust_nation, l_year order by supp_nation, cust_nation, l_year